binding.response:
let quantity: u32 = request.body.quantity;
let result = golem:cart-exports/api.{add-item}(request.path.product-id, quantity);
match result {  ok(_) => {status: 200u64, body: {message: "item added"}}, err(x) => {status: 400u64, body: {error: x}} }

let email: string = request.body.email;
let result = golem:cart-exports/api.{update-email}(email);
match result {  ok(_) => {status: 200u64, body: {message: "email set"}}, err(x) => {status: 400u64, body: {error: x}} }
