binding.response:
let email: string = request.body.email;
let result = golem:order-exports/api.{update-email}(email);
match result {  ok(_) => {status: 200u64, body: ok({message: "email set"})}, err(x) => {status: 400u64, body: err({error: x})} }

let quantity: u32 = request.body.quantity;
let result = golem:order-exports/api.{add-item}(request.path.product-id, quantity);
match result {  ok(_) => {status: 200u64, body: ok({message: "item added"})}, err(x) => {status: 400u64, body: err({error: x})} }
